// SBT 相关函数签名
export const SBT_FUNCTIONS = {
  getUserSBTs: "getUserSBTs(address)",
  getSBTDepositInfo: "getSBTDepositInfo(uint256)",
  getUserStats: "getUserStats(address)",
  tokenURI: "tokenURI(uint256)",
  ownerOf: "ownerOf(uint256)",
  balanceOf: "balanceOf(address)",
  totalSupply: "totalSupply()",
  name: "name()",
  symbol: "symbol()",
  getBaseURI: "getBaseURI()",
  setBaseURI: "setBaseURI(string)",
} as const;

// SBT 相关事件
export const SBT_EVENTS = {
  SBTMinted: "SBTMinted(address,uint256,uint256)",
  Transfer: "Transfer(address,address,uint256)",
  BaseURIUpdated: "BaseURIUpdated(string)",
  Deposited: "Deposited(address,uint256,uint256,uint256,uint256)", // 注意：已包含 sbtTokenId
} as const;

// TypeScript 类型定义
export interface SBTDepositInfo {
  depositor: string;
  amount: bigint;
  timestamp: bigint;
  depositNumber: bigint;
  sbtTokenId: bigint;
}

export interface UserStats {
  totalDeposits: bigint;
  totalAmount: bigint;
  sbtCount: bigint;
}

export interface SBTInfo {
  tokenId: bigint;
  depositNumber: bigint;
  depositInfo: SBTDepositInfo;
  tokenURI: string;
  owner: string;
}

// 格式化工具函数
export const formatSBTData = {
  // 格式化用户统计信息
  formatUserStats: (stats: UserStats) => ({
    totalDeposits: Number(stats.totalDeposits),
    totalAmount: Number(stats.totalAmount) / 1e18, // 转换为 USDT
    sbtCount: Number(stats.sbtCount),
  }),

  // 格式化充值信息
  formatDepositInfo: (info: SBTDepositInfo) => ({
    depositor: info.depositor,
    amount: Number(info.amount) / 1e18, // 转换为 USDT
    timestamp: new Date(Number(info.timestamp) * 1000),
    depositNumber: Number(info.depositNumber),
    sbtTokenId: Number(info.sbtTokenId),
  }),
};
